function getLang(): string {
    global $translations;
    $lang = $_GET['lang'] ?? $_COOKIE['lang'] ?? 'ja';
    return isset($translations[$lang]) ? $lang : 'ja';
}

function t(string $key): string {
    global $translations;
    $lang = getLang();
    return $translations[$lang][$key] ?? $translations['ja'][$key] ?? $key;
}

function getLangDict(): array {
    global $translations;
    $lang = getLang();
    return $translations[$lang] ?? $translations['ja'];
}

